    //   requestAnimationFrame( animate );
    //   cube.rotation.x += 0.01;
    //   cube.rotation.y += 0.01;
    //   cube.rotation.z += 0.01;
    //   renderer.render( scene, camera );
    // };
    // animate();
    // === THREE.JS EXAMPLE CODE END ===
  }
  render() {
    return (
      <div className="viewport"></div>
    )
  }
}
export default App
