import React, { Component } from "react";
// import ReactDOM from "react-dom";
import * as THREE from "three";
import { OrbitControls } from "three/examples/jsm/controls/OrbitControls";
// import { Vector3 } from "three";
import './App.css'

class App extends Component {
  componentDidMount() {
    // === THREE.JS CODE START ===
    var scene = new THREE.Scene();
    // const canvas = document.querySelector('viewport');
    var camera = new THREE.PerspectiveCamera( 75, window.innerWidth/window.innerHeight, 0.1, 1000 );
    // var renderer = new THREE.WebGLRenderer({ canvas });
    var renderer = new THREE.WebGLRenderer( { antialias: true } );
    scene.background = new THREE.Color('grey');
    renderer.setSize( window.innerWidth, window.innerHeight );
    document.body.appendChild( renderer.domElement );
    renderer.render( scene, camera );

    camera.position.z = 2;
    camera.position.y = 1.5;
    camera.position.x = 1;
    camera.lookAt(new THREE.Vector3(0, 0, 0));
    const controls = new OrbitControls( camera, renderer.domElement );

    // var overhead = new THREE.AmbientLight( {color: 0xffffff}, 2);
    // scene.add( overhead );

    const lightL = new THREE.DirectionalLight( 0x404040, 5); // soft white light
    const lightR = new THREE.DirectionalLight( 0x404040, 5); // soft white light
    lightL.position.x = -1;
    lightR.position.x = 1;
    scene.add( lightL );
    scene.add( lightR );

    var x = -1
    var ballMesh = new THREE.SphereGeometry( 0.04 );
    // var ballMat = new THREE.MeshBasicMaterial( { color: 0xdadada } );
    var ballMat = new THREE.MeshPhongMaterial( { color: 0xdadada } );
    for (var i = 0; i < 20; i++) {
      window['cube'+i] = new THREE.Mesh( ballMesh, ballMat );
      eval('cube'+i).position.x = (x+(i/15));
      eval('cube'+i).position.y = (0.1+(i/15));
      // eval('cube'+i).scale.set(i/10, i/10, i/10);
      eval('cube'+i).castShadow = true;
      eval('cube'+i).receiveShadow = true;
      scene.add( eval('cube'+i) );
      console.log(eval('cube'+i));
      renderer.render( scene, camera );
    }

    var tableMesh = new THREE.BoxGeometry( 2.74, 0.05, 1.526 );
    var tableMat = new THREE.MeshPhongMaterial( {color: 0x335f51});
    var table = new THREE.Mesh( tableMesh, tableMat );
    table.castShadow= true;
    table.receiveShadow = true;
    scene.add( table );

    function animate()
    {
        controls.update();
        requestAnimationFrame ( animate );  
        renderer.render (scene, camera);
    }
    animate();

    // var geometry = new THREE.BoxGeometry( 2, 2, 2 );
    // var material = new THREE.MeshBasicMaterial( { color: 0xdadada } );
    // var cube = new THREE.Mesh( geometry, material );
    // cube.castShadow = true;
    // cube.receiveShadow = true;
    // scene.add( cube );
    // camera.position.z = 5;
    // var animate = function () {
    //   requestAnimationFrame( animate );
    //   cube.rotation.x += 0.01;
    //   cube.rotation.y += 0.01;
    //   cube.rotation.z += 0.01;
    //   renderer.render( scene, camera );
    // };
    // animate();
    // === THREE.JS EXAMPLE CODE END ===
  }
  render() {
    return (
      <div className="viewport"></div>
    )
  }
}
export default App
